package MyProject;

public class IncreDecre {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=6;
		System.out.println("x++ = "+ x++);
		System.out.println("++x = "+ ++x);
		System.out.println("x-- = "+ x--);
		System.out.println("--x = "+ --x);
	}

}
